import {View, Text, StyleSheet, Image, TextInput, Button, TouchableOpacity} from 'react-native';

export default function App(){
  const nomes = ["Cicrano","Furano","Bertrano","Tobias","Josefino","Juzessara"];
  const imgGato = require('./assets/gato.jpg');
  const trashIcon = require('./assets/siii.png');

  return (
   <View style={style.container}>
    <View style={style.primeira}>
      <Text>Ol√° Lindaaaa !!</Text>
      <TextInput style={style.input}  placeholder='ESCREVA AQUI KKK' multiline={true} numberOfLines={10}/>
      < Button/>
      <TouchableOpacity >
        <Image style={{width: '40px', height: '40px'}} source={trashIcon}/>
        <Text>Apagar hehehe</Text>
      </TouchableOpacity>
      {
        nomes.map(nome =>{
        return (
          <Text>{nome}</Text>
        )
        })
      }
      </View>
      <View style={style.segunda}>
        <Text> Oi :D, tim :c, claro :o</Text>
      <Image  style={style.imgStyle} source={imgGato} />
      <Image  style={style.imgStyle} source= {{uri:'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQjTdLufpxcGG4cD497yh6GxxTeOfw6p8HdOe-6R43ytm2Q4jjNHM8cqOZGvns_pM7A5H4&usqp=CAU'}}/>
      </View>
      </View>
  );
}
const style = StyleSheet.create({
  container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
  },

  primeira: {

    backgroundColor: '#F1D2E7'

  },
  segunda: {

      backgroundColor: '#e2f5c8'
  },
  imgStyle: {
          width: '300px',
          height: '300px',
  },
  input:{
    border:'1px solid black',
    margin:'25px',
    padding:'1px',


  },
})
